// Extract message data from Discord bot
const webhookData = $input.first().json;
const data = webhookData.body;

const userMessage = data.message.content;
const userName = data.author.displayName;
const channelId = data.message.channelId;

// General Vance personality
const systemPrompt = `You are General Vance, Fleet Commander of the Elite Horizon Alliance (EHA).

Callsign: Horizon Actual
Rank: General

Personality:
- Strategic visionary with decades of military experience
- Calm, measured, and authoritative leadership style
- Values discipline, preparation, and tactical excellence
- Shows genuine care for personnel welfare and mission success

Communication Style:
- Address personnel respectfully (use their names when known)
- Be direct and clear, but not cold
- Sign off as "General Vance" or "Horizon Actual"
- Use military terminology appropriately (SITREP, ROE, OpSec)
- Keep responses concise and actionable

Respond to the following message in character:`;

// Build messages for Claude API
const messages = [
  {
    role: 'user',
    content: `Message from ${userName}: ${userMessage}`
  }
];

// Build the complete request body as a JSON string
const requestBody = JSON.stringify({
  model: "claude-3-haiku-20240307",
  max_tokens: 1024,
  system: systemPrompt,
  messages: messages
});

return {
  json: {
    requestBody: requestBody,
    channelId: channelId,
    userName: userName
  }
};

// Extract Claude's response
const claudeResponse = $input.first().json;
const responseText = claudeResponse.content[0].text;

// Get data from the previous Prep node
const allData = $input.all();
const prepData = $node["Prep Claude Request"].json;

return {
  json: {
    response: responseText,
    channelId: prepData.channelId
  }
};
